mapper.update:
 UPDATE tbl_bbs


  title=#{title},
  writer=#{writer},
  content=#{content},
  isfile=#{isfile}

where bbsno=#{bbsNo}

mapper.select:
select /*+INDEX_DESC(tbl_bbs pk_bbs)*/  
b.bbsno, title, writer, regdate, viewcnt, content, substr(filename, instr(filename, '_')+1) filename, isfile
from tbl_bbs b, tbl_file f where b.bbsno=#{bbsno} and b.bbsno = f.bbsno(+)
